export function FeaturesSection() {
  return (
    <section className="flex flex-col items-center w-full py-12 md:py-24 px-4 bg-white">
      <h2 className="text-4xl md:text-5xl lg:text-6xl font-bold tracking-tight text-center mb-8 md:mb-16 text-cefer-black">
        Everything You Need to <span className="text-gradient-cefer py-1">Win More Deals</span>
      </h2>

      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-x-8 gap-y-8 md:gap-y-16 max-w-[1100px] w-full">
        {features.map((feature, index) => (
          <Link 
            key={index} 
            to={feature.route} 
            className="no-underline"
            onClick={() => {
              window.scrollTo(0, 0);
              window.dispatchEvent(new Event('scroll'));
            }}
          >
            <div className="flex flex-col items-center text-center group cursor-pointer">
              <div className="w-16 h-16 bg-white rounded-full shadow-[0_4px_16px_rgba(0,0,0,0.15)] flex items-center justify-center mb-6 text-cefer-blue transition-transform group-hover:scale-110">
                {feature.icon}
              </div>
              <h3 className="text-xl font-bold text-cefer-black mb-3">
                {feature.title}
              </h3>
              <p className="text-sm text-neutral-500 leading-relaxed max-w-[240px]">
                {feature.description}
              </p>
            </div>
          </Link>
        ))}
      </div>
    </section>
  );
}
